import {GET_CONTACT} from './actionTypes'
import axios from 'axios'

export const getcontacts=()=>async(dispatch)=>{
    try {
       const res=await axios.get('/api/users/getuser') 
       dispatch({
           type : GET_CONTACT,
           payload : res.data
       })
    } catch (error) {
        console.log(error)
    }

}
export const addcontacts=()=>async(dispatch)=>{
    try {
     await axios.post('/api/users/adduser')   
    } catch (error) {
        console.log(error)
    }
}
